// Game.js
import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import axios from 'axios';
import config from '../configs/config';

export default function Game() {
    const [options, setOptions] = useState([]);
    const [selectedOption, setSelectedOption] = useState(null);
    const [guessResult, setGuessResult] = useState(null);

    useEffect(() => {
        axios.get(`${config.url}/mesta/all`)
            .then(response => {
                const cityOptions = response.data.mesta.map(city => ({
                    value: city.nazev,
                    label: city.nazev
                }));
                setOptions(cityOptions);
            })
            .catch(error => console.error('Chyba při načítání měst:', error));
    }, []);

    const handleGuessSubmit = () => {
        if (!selectedOption) {
            alert("Žádné město nebylo vybráno");
            return;
        }

        axios.post(`${config.url}/mesta/guess`, { guess: selectedOption.value })
            .then(response => {
                setGuessResult(response.data);
            })
            .catch(error => console.error('Chyba při odesílání tipu:', error));
    };

    return (
        <div className='Game'>
            <h2>MESTODLE</h2>
            <Select 
                options={options}
                value={selectedOption}
                onChange={setSelectedOption}
                className='select'
            />
            <button onClick={handleGuessSubmit}>Odeslat typ uživatele</button>

            {guessResult && (
                <div className='result'>
                    <p>{guessResult.message}</p>
                    {/* Zde můžete přidat logiku pro zobrazení podobností */}
                </div>
            )}
        </div>
    );
}
