import React from 'react';

function getStatusClass(comparison) {
  switch (comparison) {
    case "=":
      return "correct";
    case "<":
      return "incorrect-less";
    case ">":
      return "incorrect-greater";
    default:
      return "";
  }
}

export default function CityDetails({ nazev, kraj, populace, rozloha, similarites }) {
  if (!similarites) {
    console.log("Waiting for data...");
    return <div>Loading...</div>;
  }

  console.log("Displaying city details:", { nazev, kraj, populace, rozloha, similarites });
  return (
    <div className="d-flex justify-content-center"> {/* Přidán wrapper pro centrování */}
      <div className="text-center mb-2"> {/* Upravený kontejner */}
        <div className="row">
          <div className="col-3 cityData"> {/* Upravená šířka sloupce */}
            <span>Název města: {nazev}</span>
          </div>
          <div className={`col-2 cityData ${similarites.sameRegion ? "correct" : "incorrect"}`}>
            <span>Kraj: {kraj}</span>
          </div>
          <div className={`col-2 cityData ${getStatusClass(similarites.samePopulace)}`}>
            <span>Populace: {populace}</span>
          </div>
          <div className={`col-2 cityData ${getStatusClass(similarites.sameRozloha)}`}>
            <span>Rozloha: {rozloha}</span>
          </div>
        </div>
      </div>
    </div>
  );
}
