import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import makeAnimated from 'react-select/animated';
import axios from 'axios';
import config from './configs/config';
import './App.css';

const animatedComponents = makeAnimated();

export default function CitySelect() {
    const [options, setOptions] = useState([]);
    const [selectedOption, setSelectedOption] = useState(null);
    const [randomMesto, setRandomMesto] = useState(null);

    const startGame = () => {
      axios.get(`${config.url}/mesta/startGame`)
        .then(response => {
          if (response.data.status === 'success') {
            setRandomMesto(response.data.randomMesto.nazev);
          }
        })
        .catch(error => console.error('Chyba při zahájení hry:', error));
    };

    useEffect(() => {
      startGame();
    }, []);

    useEffect(() => {
        axios.get(`${config.url}/mesta/all`)
            .then(response => {
                const cityOptions = response.data.mesta.map(city => ({
                    value: city.nazev,
                    label: city.nazev
                }));
                setOptions(cityOptions);
            })
            .catch(error => console.error('Chyba při načítání měst:', error));
    }, []);

    const handleGuessSubmit = () => {
        if (!selectedOption) {
          alert("Žádné město nebylo vybráno");
          return;
        }

        axios.post(`${config.url}/mesta/guess`, { guess: selectedOption.value })
          .then(response => {
            if (response.data.correctGuess) {
              alert("Správně! Uhodl jsi město.");
            } else {
              if (response.data.attempts >= 5) {
                alert("Konec hry. Překročil jsi maximální počet pokusů.");
              } else {
                alert("Špatně. Zkus to znovu.");
              }
            }
          })
          .catch(error => console.error('Chyba při odesílání tipu:', error));
      };

    return (
        <div className='App'>
            <h2>MESTODLE</h2>
            <Select 
                components={animatedComponents}
                options={options}
                value={selectedOption}
                onChange={setSelectedOption}
                className='select'
            />
            <button onClick={handleGuessSubmit}>Odeslat typ uživatele</button>
            {randomMesto && <p>Aktuální hra: Uhodněte město - {randomMesto}</p>}
        </div>
    );
}
