import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Select from 'react-select';
import CityDetails from './components/cityDetails';
import config from './configs/config';

export default function Game() {
  const [cities, setCities] = useState([]);
  const [currentGuess, setCurrentGuess] = useState(null);
  const [correctCity, setCorrectCity] = useState(null);
  const [isCorrect, setIsCorrect] = useState(false);

  useEffect(() => {
    fetchCities();
  }, []);

  const fetchCities = () => {
    axios.get(`${config.url}/mesta/all`)
      .then(response => {
        setCities(response.data.mesta);
      })
      .catch(error => console.error('Chyba při načítání měst:', error));
  };

  const handleGuess = () => {
    axios.post(`${config.url}/mesta/guess`, { guess: currentGuess })
      .then(response => {
        if (response.data.isCorrect) {
          setCorrectCity(response.data.city);
          setIsCorrect(true);
        } else {
          // Zpracování nesprávného tipu
        }
      })
      .catch(error => console.error('Chyba při hádání:', error));
  };

  return (
    <div>
      <Select 
        options={cities.map(city => ({ label: city.nazev, value: city.nazev }))}
        onChange={setCurrentGuess}
      />
      <button onClick={handleGuess}>Hádej</button>
      {isCorrect && <CityDetails city={correctCity} />}
    </div>
  );
}
