import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import axios from 'axios';
import Titles from "./gameTitle";
import CityDetails from "./cityDetails";
import { Helmet } from "react-helmet";
import config from '../configs/config';
import { SelectStyles, customFilterOption } from "./selectStyles";

export default function Game() {
    const [validGuesses, setValidGuesses] = useState([]);
    const [cities, setCities] = useState([]);
    const [guesses, setGuesses] = useState([]);
    const [currentGuess, setGuess] = useState(validGuesses[0]);
    const [correctGuess, setCorrectGuess] = useState(false);
    //const [title, setTitle] = useState("");
    
    useEffect(() => {
        FetchCities();
    }, []); // eslint-disable-line react-hooks/exhaustive-deps

    const FetchCities = () => {
        axios.get(`${config.url}/mesta/all`)
            .then(response => {
                if (response.data.status === "success") {
                    const data = response.data.mesta;
                    data.sort((a, b) => a.nazev.localeCompare(b.nazev));
                    const transformedData = data.map(city => ({
                        value: city.nazev,
                        label: city.nazev
                    }));
                    setValidGuesses(transformedData);
                }
            })
            .catch(error => {
                console.error('Chyba při načítání měst:', error);
            });
    };

    const Guess = async (e) => {
      e.preventDefault();
  
      if (!currentGuess || guesses.includes(currentGuess)) {
          return;
      }
  
      setGuesses((guesses) => [...guesses, currentGuess]);
  
        try {
          const response = await axios.post(`${config.url}/mesta/guess`, { guess: currentGuess });
          const { correctGuess, properties } = response.data;

          if (properties && properties.length === 2) {
              const [mestoData, similarites] = properties;
              setCities((cities) => [[mestoData, similarites], ...cities]);
          }

          setCorrectGuess(correctGuess);
      } catch (error) {
          console.error('Error during guess:', error);
      }
  };
    
    const Reroll = async () => {
      setCities([]); // Reset seznamu měst
      setGuess(null); // Reset aktuálního hádaní
      setCorrectGuess(false); // Reset stavu správného uhádnutí
  
      try {

          const response = await axios.get(`${config.url}/mesta/random`);
          if (response.data.status === "success") {
            const randomCity = response.data.mesto;

            // Aktualizace států pro zobrazení informací o novém městě
            setCities([{
                guessedMesto: randomCity.nazev, // nebo jiný klíč, záleží na struktuře dat
                populace: randomCity.populace,
                rozloha: randomCity.rozloha,
                kraj: randomCity.krajId
            }]);

            // Resetování ostatních relevantních států
            setCorrectGuess(false);
            setGuess(null);
          }
      } catch (error) {
          console.error('Chyba při provádění Reroll:', error);
      }
    };

    const Restart = () => {
        FetchCities();
        setGuesses([]);
        setCities([]);
        setGuess();
        setCorrectGuess(false);
    };

    return (
        <div className="container main pt-4 pb-5 mb-5">
      <Helmet>
        <title>
          MESTODLE
        </title>
        <meta
          name="description"
          content=""
        />
      </Helmet>

      <h3 className="text-center pb-3">MESTODLE</h3>

      <div className="d-flex justify-content-center mt-4 mb-3">
        <form
          className="form-control row g-3 mb-4"
          onSubmit={Guess}
          id="guess-form"
        >
          <Select
            className="select"
            options={validGuesses}
            onChange={(selectedOption) => setGuess(selectedOption.value)}
            isDisabled={correctGuess}
            styles={SelectStyles}
            placeholder="Napiš jméno města"
            filterOption={customFilterOption}
            formatOptionLabel={(data) => (
              <div className="select-option">
                <span>{data.label}</span>
              </div>
            )}
          />
          <div className="d-flex justify-content-evenly">
            {correctGuess ? (
                <button className="btn btn-outline-dark mb-3 mt-1 min-vw-25" onClick={Restart}>Next</button>
            ) : (
                <button className="btn btn-dark mb-3 mt-1 min-vw-25">Guess</button>
            )}
            {!correctGuess && guesses.length >= 10 ? (
                <button className="btn btn-dark mb-3 mt-1 min-vw-25" onClick={Reroll}>Reroll</button>
            ) : (
                ""
            )}
        </div>
        </form>
      </div>

      {cities.length > 0 ? <Titles /> : ""}
      <div id="cities">
        {cities.map(([mestoData, similarites], index) => (
            <CityDetails
            key={index} 
            kraj={mestoData.kraj_id}
            populace={mestoData.populace}
            rozloha={mestoData.rozloha}
            similarites={similarites}
            />
        ))}
        </div>
    </div>
    );
}
