import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import axios from 'axios';
import Titles from "./gameTitle";
import CityDetails from "./cityDetails";

import { Helmet } from "react-helmet";
import { useSelector } from "react-redux";
import config from '../configs/config';


export default function Game() {
    const [validGuesses, setValidGuesses] = useState([]);
    const [cities, setCities] = useState([]);
    const [guesses, setGuesses] = useState([]);
    const [currentCity, setCurrentCity] = useState("");
    const [currentGuess, setGuess] = useState(validGuesses[0]);
    const [correctGuess, setCorrectGuess] = useState(false);
    const [title, setTitle] = useState("");

    useEffect(() => {
        FetchCities();
    }, []);

    const FetchCities = () => {
        axios.get(`${config.url}/mesta/all`)
            .then(response => {
                setOptions(cityOptions);
                if (response.data.status === "success") {
                    const data = response.data.mesta;
                    data.sort((a, b) => a.value.localeCompare(b.value));
                    const transformedData = data.map(city => ({
                        value: city.nazev,
                        label: city.nazev
                    }));
                    setValidGuesses(transformedData);
                  }
            })
            .catch(error => {
                console.error('Chyba při načítání měst:', error);
            });
    };

        axios.post(`${config.url}/mesta/guess`, { guess: selectedOption.value })
        .then(response => {
            setGuessResult(response.data.message);
            if (response.data.correctGuess) {
                setGameOver(true);
            }
            
            if (response.data.currentMesto) {
                setCurrentCity(response.data.currentMesto.nazev);
            }
        })
        .catch(error => {
            console.error('Chyba při odesílání tipu:', error);
        });
        

    const Guess = (e) => {
        e.preventDefault();
    
        if (!currentGuess) {
          return;
        }
    
        if (guesses.indexOf(currentGuess) !== -1) {
          return;
        }
    
        setValidGuesses(validGuesses.filter((item) => item.label !== currentGuess));
        setGuesses((guesses) => [...guesses, currentGuess]);
    
        axios.post(`${config.url}/mesta/guess`,
            { guess: currentGuess }
          )
          .then(response => {
            if (response.data.status !== "success") {
              return;
            }
    
            const correct = response.data.correctGuess;
            const data = response.data.properties;
    
            setChampions((champions) => [data, ...champions]);
    
            if (correct) {
              if (guesses.length === 0) {
              }
              setCorrectGuess(true);
              setTitle(response.data.title);
            }
          })
          .catch((error) => {
            console.log(error);
            setChampions([]);
          });
    };

    const Restart = () => {
        FetchChampions();
    
        setGuesses([]);
        setChampions([]);
        setGuess();
        setCorrectGuess(false);
    };

    return (
        <div className="container main pt-4 pb-5 mb-5">
      <Helmet>
        <title>
          Infiniteloldle - Ultimate LoL quiz - Champion guessing game
        </title>
        <meta
          name="description"
          content="The ultimate quiz game for League of Legends enthusiasts. Guess League of Legends champions infinitely."
        />
      </Helmet>

      <h3 className="text-center pb-3">Start guessing your champion</h3>

      <div className="d-flex justify-content-center mt-4 mb-3">
        <form
          className="form-control row g-3 mb-4"
          onSubmit={Guess}
          id="guess-form"
        >
          <Select
            className="select"
            options={validGuesses}
            onChange={(selectedOption) => setGuess(selectedOption.value)}
            isDisabled={correctGuess}
            styles={SelectStyles}
            placeholder="Type champions name"
            filterOption={customFilterOption}
            formatOptionLabel={(data) => (
              <div className="select-option">
                <LazyLoad offset={200}>
                  <img
                    src={"/40_40/champions/" + data.image + ".webp"}
                    alt="Champion icon"
                  />
                </LazyLoad>
                <span>{data.label}</span>
              </div>
            )}
          />

          <div className="d-flex justify-content-evenly">
            {correctGuess ? (
              <>
                <button
                  className="btn btn-outline-dark mb-3 mt-1 min-vw-25"
                  onClick={Restart}
                >
                  Next
                </button>
              </>
            ) : (
              <button className="btn btn-dark mb-3 mt-1 min-vw-25">
                Guess
              </button>
            )}
            {!correctGuess && guesses.length >= 10 ? (
              <button
                className="btn btn-dark mb-3 mt-1 min-vw-25"
                onClick={() => Reroll("champion")}
              >
                Reroll
              </button>
            ) : (
              ""
            )}
          </div>
        </form>
      </div>

      {champions.length > 0 ? <Titles /> : ""}

      <div id="champions">
        {champions.map((champ) => (
          <ChampionDetails
            key={champ[0].championKey}
            championKey={champ[0].championKey}
            gender={champ[0].gender}
            genre={champ[0].genre}
            resource={champ[0].resource}
            rangeTypes={champ[0].rangeType}
            positions={champ[0].position}
            releaseYear={champ[0].releaseYear}
            regions={champ[0].region}
            damageType={champ[0].damageType}
            similarites={champ[1]}
            isColorBlindMode={isColorBlindMode}
            hideResource={hideResource}
          />
        ))}
      </div>

      {correctGuess ? (
        <Victory
          id="victory"
          championKey={champions[0][0].championKey}
          champion={champions[0][0].guessedChampion}
          tries={guesses.length}
          title={title}
        />
      ) : (
        ""
      )}
    </div>
    );
}
