import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AutoCompleteTextBox = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [cities, setCities] = useState([]);

  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      // Send the query after the user has stopped typing for 300ms
      if (searchTerm) {
        axios
          .get(`http://localhost:8081/api/mesta?term=${searchTerm}`)
          .then((response) => {
            setCities(response.data);
          })
          .catch((error) => {
            console.error('Error fetching cities:', error);
          });
      } else {
        setCities([]); // Clear suggestions when input is empty
      }
    }, 300);

    return () => clearTimeout(delayDebounceFn); // Cleanup the timeout on component unmount
  }, [searchTerm]);

  return (
    <div className="autocomplete">
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder="Enter city..."
        className="autocomplete-input"
      />
      {searchTerm && cities.length > 0 && (
        <ul className="autocomplete-results">
          {cities.map((city) => (
            <li key={city.id} className="autocomplete-item">
              {city.nazev}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AutoCompleteTextBox;
