import React from 'react';

// Pomocná funkce pro určení třídy CSS podle porovnání
function getStatusClass(comparison) {
  switch (comparison) {
    case "=":
      return "correct";
    case "<":
      return "incorrect-less";
    case ">":
      return "incorrect-greater";
    default:
      return "";
  }
}

// Komponenta CityDetails
export default function CityDetails({ kraj, populace, rozloha, similarites }) {
  // Zobrazí "Loading..." pokud nejsou předány všechny potřebné props
  if (!similarites || kraj === undefined || populace === undefined || rozloha === undefined) {
    return <div>Loading...</div>; 
  }

  return (
    <div className="container text-center mb-2">
      <div className="row">
        <div className={`col cityData ${similarites.sameRegion ? "correct" : "incorrect"}`}>
          <span>Kraj: {kraj}</span>
        </div>
        <div className={`col cityData ${getStatusClass(similarites.samePopulace)}`}>
          <span>Populace: {populace}</span>
        </div>
        <div className={`col cityData ${getStatusClass(similarites.sameRozloha)}`}>
          <span>Rozloha: {rozloha}</span>
        </div>
      </div>
    </div>
  );
}
