import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AutocompleteTextbox = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [suggestions, setSuggestions] = useState([]);

    useEffect(() => {
        if (searchTerm.length > 1) { // Pro získání návrhů po zadání alespoň dvou znaků
            axios.get(`http://localhost:8081/api/mesta?term=${searchTerm}`)
                .then(response => {
                    setSuggestions(response.data);
                })
                .catch(error => {
                    console.error('Chyba při načítání návrhů:', error);
                });
        } else {
            setSuggestions([]);
        }
    }, [searchTerm]);

    return (
        <div>
            <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Začněte psát název města..."
            />
            {suggestions && suggestions.length > 0 && (
                <ul>
                    {suggestions.map((suggestion, index) => (
                        <li key={index} onClick={() => setSearchTerm(suggestion.nazev)}>
                            {suggestion.nazev}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default AutocompleteTextbox;
