import React, { useState, useEffect } from "react";
import axios from "axios";
import Select from "react-select";

export default function Game() {
  const [validGuesses, setValidGuesses] = useState([]);
  const [cities, setCities] = useState([]);
  const [guesses, setGuesses] = useState([]);
  const [currentGuess, setCurrentGuess] = useState(null);
  const [correctGuess, setCorrectGuess] = useState(false);
  const [randomCity, setRandomCity] = useState(null);

  useEffect(() => {
    fetchCities();
    selectRandomCity();
  }, []);

  const fetchCities = () => {
    axios.get("http://localhost:8081/api/mesta")
      .then(response => {
        const transformedCities = response.data.map(city => ({
          value: city.nazev,
          label: city.nazev
        }));
        setValidGuesses(transformedCities);
      })
      .catch(error => console.error("Chyba při načítání měst:", error));
  };

  const selectRandomCity = () => {
    axios.get("http://localhost:8081/api/mesta/random")
      .then(response => setRandomCity(response.data.nazev))
      .catch(error => console.error("Chyba při výběru náhodného města:", error));
  };

  const handleGuess = (e) => {
    e.preventDefault();
    if (!currentGuess) return;

    setGuesses([...guesses, currentGuess]);

    if (currentGuess.value === randomCity) {
      setCorrectGuess(true);
    }
  };

  const restartGame = () => {
    setGuesses([]);
    setCorrectGuess(false);
    selectRandomCity();
  };

  return (
    <div className="game-container">
      <h1>Hádej město</h1>
      <Select
        options={validGuesses}
        onChange={option => setCurrentGuess(option)}
        value={currentGuess}
        isDisabled={correctGuess}
      />
      <button onClick={handleGuess} disabled={correctGuess}>Hádej</button>
      {correctGuess && (
        <div>
          <p>Gratulujeme! Správně jsi uhodl: {randomCity}</p>
          <button onClick={restartGame}>Začít znovu</button>
        </div>
      )}
      <div>
        <p>Předchozí tipy:</p>
        <ul>
          {guesses.map((guess, index) => <li key={index}>{guess.label}</li>)}
        </ul>
      </div>
    </div>
  );
}
