import React, { useState, useEffect } from "react";
import axios from "axios";
import Select from "react-select";
import { useSelector } from "react-redux";

export default function Game() {
  const [validGuesses, setValidGuesses] = useState([]);
  const [mesta, setMesta] = useState([]);
  const [guesses, setGuesses] = useState([]);
  const [currentGuess, setCurrentGuess] = useState("");

  useEffect(() => {
    fetchMesta();
  }, []);

  const fetchMesta = () => {
    axios
      .get("/api/mesta/all")
      .then((response) => {
        if (response.data.status === "success") {
          const transformedData = response.data.mesta.map((mesto) => ({
            value: mesto.id,
            label: mesto.nazev,
          }));
          setValidGuesses(transformedData);
        }
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const guessMesto = (e) => {
    e.preventDefault();

    if (!currentGuess) {
      return;
    }

    if (guesses.includes(currentGuess)) {
      return;
    }

    setValidGuesses(validGuesses.filter((item) => item.value !== currentGuess));
    setGuesses([...guesses, currentGuess]);

    // Zde byste měli zpracovat hádání uživatele
    // Tento kód je jen placeholder, budete potřebovat implementovat logiku na serveru
    axios
      .post("/api/mesta/guess", { guess: currentGuess })
      .then((response) => {
        // Zpracování odpovědi serveru
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const restartGame = () => {
    fetchMesta();
    setGuesses([]);
    setMesta([]);
    setCurrentGuess("");
  };

  return (
    <div className="container main pt-4 pb-5 mb-5">
      <h3 className="text-center pb-3">Hádejte město</h3>

      <div className="d-flex justify-content-center mt-4 mb-3">
        <form onSubmit={guessMesto} id="guess-form">
          <Select
            options={validGuesses}
            onChange={(selectedOption) => setCurrentGuess(selectedOption.value)}
            styles={SelectStyles}
            placeholder="Zadejte název města"
            filterOption={customFilterOption}
          />
          <button className="btn btn-dark mb-3 mt-1 min-vw-25">
            Hádat
          </button>
        </form>
      </div>

      {/* Zde zobrazte seznam měst a jejich vlastností */}
      <div id="mesta">
        {/* Místo pro komponenty s podrobnostmi o městech */}
      </div>

      {/* Tlačítko pro restart hry */}
      <button
        className="btn btn-outline-dark mb-3 mt-1 min-vw-25"
        onClick={restartGame}
      >
        Restartovat hru
      </button>
    </div>
  );
}
