import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AutoCompleteTextBox.css'; // Make sure your CSS file is correctly referenced

const AutoCompleteTextBox = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [cities, setCities] = useState([]);

  useEffect(() => {
    if (searchTerm) {
      // Only start searching if searchTerm has been entered
      const fetchCities = async () => {
        try {
          const response = await axios.get(`http://localhost:8081/api/mesta?term=${searchTerm}`);
          setCities(response.data);
        } catch (error) {
          console.error('Error fetching cities:', error);
        }
      };

      const timerId = setTimeout(fetchCities, 500); // Add a debounce time of 500ms
      return () => clearTimeout(timerId);
    } else {
      setCities([]); // Clear suggestions when input is empty
    }
  }, [searchTerm]);

  return (
    <div className="autocomplete">
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder="Enter city..."
        className="autocomplete-input"
      />
      {searchTerm && cities.length > 0 && (
        <ul className="autocomplete-results">
          {cities.map((city) => (
            <li key={city.id} className="autocomplete-item">
              {city.nazev}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AutoCompleteTextBox;
