import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import axios from 'axios';
import config from '../configs/config';

export default function Game() {
    const [options, setOptions] = useState([]);
    const [selectedOption, setSelectedOption] = useState(null);
    const [guessResult, setGuessResult] = useState(null);
    const [gameOver, setGameOver] = useState(false);
    const [currentCity, setCurrentCity] = useState("");

    useEffect(() => {
        axios.get(`${config.url}/mesta/all`)
            .then(response => {
                const cityOptions = response.data.mesta.map(city => ({
                    value: city.nazev,
                    label: city.nazev
                }));
                setOptions(cityOptions);
            })
            .catch(error => {
                console.error('Chyba při načítání měst:', error);
            });
    }, []);

    const handleGuessSubmit = () => {
        if (!selectedOption) {
            alert("Žádné město nebylo vybráno");
            return;
        }

        axios.post(`${config.url}/mesta/guess`, { guess: selectedOption.value })
        .then(response => {
            setGuessResult(response.data.message);
            if (response.data.correctGuess) {
                setGameOver(true);
            }
            setCurrentCity(response.data.currentMesto.nazev); // Ujistěte se, že zde nastavujete správnou hodnotu
        })
        .catch(error => {
            console.error('Chyba při odesílání tipu:', error);
        });
    };

    const handleRestart = () => {
        setGameOver(false);
        setGuessResult(null);
        setSelectedOption(null);
        setCurrentCity(""); // Resetuje aktuální město
    };

    return (
        <div className='App'>
            <h2>MESTODLE</h2>
            <Select 
                options={options}
                value={selectedOption}
                onChange={setSelectedOption}
                className='select'
                isDisabled={gameOver}
            />
            {!gameOver && 
                <button onClick={handleGuessSubmit}>Odeslat typ uživatele</button>
            }
            {gameOver && 
                <button onClick={handleRestart}>Restartovat hru</button>
            }
            {guessResult && 
                <p>{guessResult}</p>
            }
            <br></br>
            {currentCity && 
                <p>Aktuálně vybrané město je: {currentCity}</p>
            }
        </div>
    );
}
