import React, { useState } from 'react';
import axios from 'axios';
import Select from 'react-select';
import config from './configs/config';
import './App.css'

const CitySelect = () => {
  const [selectedOption, setSelectedOption] = useState(null);
  const [tries, setTries] = useState(0);

  const handleGuess = async () => {
    if (selectedOption) {
      try {
        const response = await axios.post(`${config.url}/mesta/guess`, { guess: selectedOption.value });
        setTries(tries + 1);

        if (response.data.correctGuess) {
          alert("Správný tip!");
        } else {
          alert("Nesprávný tip, zkuste to znovu.");
        }
      } catch (error) {
        console.error('Chyba při odesílání tipu:', error);
      }
    }
  };

  // Zde přidat kód pro načtení možností měst...

  return (
    <div className='App'>
      <h2>MESTODLE</h2>
      <Select 
        // ...
        value={selectedOption}
        onChange={setSelectedOption}
      />
      <button onClick={handleGuess}>Odeslat tip</button>
      <p>Pokusy: {tries}</p>
    </div>
  );
};

export default CitySelect;
