import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const SearchBar = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [results, setResults] = useState([]);
    const [showDropdown, setShowDropdown] = useState(false);

    const fetchCities = useCallback(async () => {
        if (!searchTerm) {
            setResults([]);
            setShowDropdown(false);
            return;
        }

        try {
            const response = await axios.get(`http://localhost:8081/api/mesta?term=${searchTerm}`);
            setResults(response.data);
            setShowDropdown(true);
        } catch (error) {
            console.error('Chyba při vyhledávání:', error);
            setShowDropdown(false);
        }
    }, [searchTerm]);

    useEffect(() => {
        fetchCities();
    }, [fetchCities]);

    return (
        <div className="search-bar">
            <input 
                type="text" 
                className="form-control"
                placeholder="Vyhledej město" 
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onFocus={() => searchTerm && setShowDropdown(true)}
            />
            {showDropdown && (
                <ul className="list-group">
                    {results.map(city => (
                        <li key={city.id} className="list-group-item" onClick={() => setSearchTerm(city.nazev)}>
                            {city.nazev}
                        </li>
                    ))}
                </ul>
            )}
            <button className="btn btn-primary mt-2">Potvrdit tip</button>
        </div>
    );
};

export default SearchBar;
